<div class="line"><a name="l00403"></a><span class="lineno">  403</span>&#160;  {</div>
<div class="line"><a name="l00404"></a><span class="lineno">  404</span>&#160;    <span class="keyword">const</span> FT_Byte*  pointer;</div>
<div class="line"><a name="l00405"></a><span class="lineno">  405</span>&#160;    FT_Int          length;</div>
<div class="line"><a name="l00406"></a><span class="lineno">  406</span>&#160;</div>
<div class="line"><a name="l00407"></a><span class="lineno">  407</span>&#160;  } <a class="code" href="structFT__Data__.php">FT_Data</a>;</div>
